//JavaScript
var alerts= document.getElementById("alerts");
var table=document.createElement("table");
table.setAttribute("class", "table table-condensed table-hover mytable");
alerts.innerHTML="";

for (i=0;i<this.datasource.results.DATA.length;i++) {
    var tr=document.createElement("tr");
    var td1=document.createElement("td");
    var td2=document.createElement("td");
    var td3=document.createElement("td");
    td1.innerHTML=this.datasource.results.DATA[i].alertmessage;
    td2.innerHTML=this.datasource.results.DATA[i].alertvalue;
    td3.innerHTML=new Date(this.datasource.results.DATA[i].alerttime).toLocaleTimeString();
    tr.appendChild(td1);
    tr.appendChild(td2);
    tr.appendChild(td3);
    if ("ExitAggregateTemp"==this.datasource.results.DATA[i].alertmessage)
        tr.setAttribute("class", "small bg-warning");
    else 
         tr.setAttribute("class", "small bg-danger");
    table.appendChild(tr);
    alerts.appendChild(table);
}

