var range=24*3; //24 hours
var d1=new Date(Date.now());
var data=[];
for (var i=0; i<range; i++)
    {
    var nt=d1.getTime() - i*(60 * 60 * 1000) //hours
        var d=new Date(nt);
        if (i>15) 
            data.unshift({"x": -i, "temp":genDP(210,150)})
        else
            data.unshift({"x": -i, "temp":genDP(410,310)})
    }
	return data;


function genDP(max,min) {
	return Math.floor(Math.random() * (max - min)) + min;
}
